user["features"].each do |point|
  bbox = point["bbox"] # [minLng, minLat, maxLng, maxLat]
  center_lng = (bbox[0] + bbox[2]) / 2.0
  center_lat = (bbox[1] + bbox[3]) / 2.0
  #p point["geometry"]["coordinates"]
